mapper.update:
update queslist
set solveDate=#{solveDate},
    answer=#{answer},
    audioUrl=#{audioUrl}
where quesID = #{quesID}

mapper.select:
select q.*, giver.photo as 'giverAvatar', solver.photo as 'solverAvatar', giver.nickname as 'giverNickname', solver.nickname as 'solverNickname' from queslist as q inner join user giver on giver.uid = q.giverID inner join user solver on solver.uid = q.solverID
and q.giverID = #{giverID}
and q.solverID = #{solverID}
and (q.quesInfo LIKE CONCAT('%',#{filter.keyword},'%')
limit #{offset},#{limit}
